function addTogether() {
  function checkNumber(num){
    return typeof num ==='number'?num:undefined
  }
  var first=checkNumber(arguments[0])
  var second=checkNumber(arguments[1])

  if(arguments.length>1){
    return first && second ? first+second:undefined
  }else{
    if(first){
      return function(y){
       if(checkNumber(y)){
         return first+y;
       }else{
         return undefined
       }
      }
    }else{
      return undefined
    }
  }


}

addTogether(2,3)
