function convertHTML(str) {
    // &colon;&rpar;
  const placeholder=str.split("")

  for(var i=0;i<placeholder.length;i++){

    switch(placeholder[i]){
        case '&':
                placeholder[i]= '&amp;'
                break;
                case '<':
                        placeholder[i]='&lt;'
                        break;
                        case '>':
                                placeholder[i]='&gt;';
                                break;
                                case '"':
                                        placeholder[i]="&quot;";
                                        break;
                                        case "'":
                                        placeholder[i]="&apos;";
                                        break;


    }

  }


    return placeholder.join('');
  }



convertHTML("Dolce & Gabbana");
