function fearNotLetter(str) {
 let charNumber=str.split('').map((letter)=>{
   return letter.charCodeAt()
 })

 for(let i=0;i<charNumber.length;i++){
   if(charNumber[i+1]-charNumber[i]>1){
     return String.fromCharCode(charNumber[i]+1)
   }


 }
 return undefined
}

