function translatePigLatin(str) {
  str=str.split('')
  let count=0
  const consonant=['b','c','d','f','g','h','j','k','l','m','n','p','q','r','s','t','v','w','x','y','z']
  for(let i=0;i<str.length;i++){
    if(consonant.indexOf(str[i])===-1){
      break;
    }else{
      count++
    }
  }
  let move=str.splice(0,count)
  if(count==0){
    str.push('way')
  }else{
    str.push(`${move.join('')}ay`)
  }
  return str.join('');
}


translatePigLatin("consonant");
